import React, { setState } from 'react';


function stateFAQ() {
    const [showElem, setElem] = useState(false);

    let Min = () => {
        return (
            <section>
                <div><span>Why is React great?</span><button onClick={setElem(true)}>+</button></div>
            </section>
        );
    };
    let Max = () => {
        return (
            <section>
                <div><span>Why is React great?</span><button onClick={setElem(false)}>+</button></div>
                <div><span>Fast Learning Curve</span></div>
            </section>
        );
    };
    // let element = setState(<Min />);
    return (
        <div>
            {showElem ? <Max /> : <Min />}
        </div>
    );
}

export default stateFAQ;
