import React, { useState } from "react";

function DayNightSwitch() {
    const [showP, setP] = useState("Es ist Tag!");
    const [showButton, setButton] = useState("Wechsel zu Nacht");
    let switchTo = () => {
        showP === "Es ist Tag!" ? setP("Es ist Nacht") : setP("Es ist Tag!");
        showButton === "Wechsel zu Nacht" ? setButton("Wechsel zu Tag") : setButton("Wechsel zu Nacht");

    };

    return (
        <div className="DaySwitcher">
            <h1>{showP}</h1>
            <button onClick={switchTo}>{showButton}</button>
        </div>
    );
}

export default DayNightSwitch;
